import React, { useState, useEffect} from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import DepartmentSelector from "./components/DepartmentSelector";
import EngineDisplayer from "./components/EngineDisplayer";

import DepartmentForm from "./components/DepartmentForm";
import CreateEngineForm from "./components/CreateEngineForm";
import UpdateEngineForm from "./components/UpdateEngineForm";

const App = () => {
  document.title = "EMNF-S"

  const sessionStoredDepartmentId = sessionStorage.getItem("sessionStoredDepartmentId")
  // const [storedDepartmentId, setStoredDepartmentId] = useState(sessionStoredDepartmentId)
  const [departments, setDepartments] = useState([])
  const [engines, setEngines] = useState([])

  useEffect(() => {
    fetch("http://localhost:9292/departments")
    .then(r => r.json())
    .then(setDepartments)
    .catch(err => alert(err.message))
  }, []);

  useEffect(() => {
    fetch(`http://localhost:9292/departments/${sessionStoredDepartmentId}/engines`)
    .then(r => r.json())
    .then(setEngines)
    .catch(err => alert(err.message))
  }, [sessionStoredDepartmentId]);

  return (
    <div>
      <h1 className="app_title">Engine Manufacturing Status Manager</h1>

      <Router>
        {/* <DepartmentSelector setStoredDepartmentId={setStoredDepartmentId} departments={departments}/> */}
        {/* <DepartmentSelector setStoredDepartmentId={setStoredDepartmentId} departments={departments}/> */}
        <Routes>
          <Route path={`/departments/${sessionStoredDepartmentId}/engines`} exact element={<EngineDisplayer sessionStoredDepartmentId={sessionStoredDepartmentId} engines={engines}/>}/> 
          <Route path={`/departments/create`} exact element={<DepartmentForm sessionStoredDepartmentId={sessionStoredDepartmentId}/>}/>
          <Route path={`/departments/${sessionStoredDepartmentId}/engines/create`} exact element={<CreateEngineForm sessionStoredDepartmentId={sessionStoredDepartmentId}/>}/>
          <Route path={`/departments/${sessionStoredDepartmentId}/engines/update`} exact element={<UpdateEngineForm sessionStoredDepartmentId={sessionStoredDepartmentId}/>}/>
        </Routes>
      </Router>
    </div>
  );
}

export default App;
